    public void Login() {
       java.sql.Connection conn = new konek().connect();
        
        try{
            String sql="select * from pengguna where username ='"+jTextField1.getText()+"' and password = '"+jPasswordField1.getText().toString()+"'";
            java.sql.Statement stmt= conn.createStatement();
            java.sql.ResultSet rslt= stmt.executeQuery(sql);
            if (rslt.next())
            {
                JOptionPane.showMessageDialog(null, "Success Login");
                new menuUtama().setVisible(true);
                dispose();
            }
            else
            {
                JOptionPane.showMessageDialog(null, "Wrong Password or Id, Please Try Again");
            }
        }    
            catch (Exception ex) {}
            
        }

